<?php

namespace AppBundle\Repository;

/**
 * SEstateAgentPhoneRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SEstateAgentPhoneRepository extends \Doctrine\ORM\EntityRepository
{
    public function findAll()
    {
        $source = $this->createQueryBuilder('p')
            ->select('p.phone')
            ->getQuery()
            ->getArrayResult();

        return array_column($source, 'phone');
    }

    public function existList(array $phones)
    {
        $source = $this->createQueryBuilder('p')
            ->select('p.phone')
            ->where('p.phone IN (:phones)')
            ->setParameter('phones', $phones)
            ->getQuery()
            ->getArrayResult();

        return array_column($source, 'phone');
    }
}
